export default function Alert(theme: any): {
    MuiAlert: {
        styleOverrides: {
            message: {
                '& .MuiAlertTitle-root': {
                    marginBottom: any;
                };
            };
            action: {
                '& button:not(:first-of-type)': {
                    marginLeft: any;
                };
            };
            standardInfo: {
                color: any;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            standardSuccess: {
                color: any;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            standardWarning: {
                color: any;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            standardError: {
                color: any;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            filledInfo: {
                color: any;
            };
            filledSuccess: {
                color: any;
            };
            filledWarning: {
                color: any;
            };
            filledError: {
                color: any;
            };
            outlinedInfo: {
                color: any;
                border: string;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            outlinedSuccess: {
                color: any;
                border: string;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            outlinedWarning: {
                color: any;
                border: string;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
            outlinedError: {
                color: any;
                border: string;
                backgroundColor: any;
                '& .MuiAlert-icon': {
                    color: any;
                };
            };
        };
    };
};
